import axios from 'axios';
import React,{useState, useEffect} from 'react';
import "./Contact.css"
// import axios from "axios"
const Contact = () =>{
  const [formData,setFormData]=useState({
    firstName:" ",
    lastName:" ",
    email:"",
    company:"",
    phone:"", 

  }) 
  

  const {firstName,lastName,email,company,phone}=formData
  const onChange = (e) => {
    setFormData({...formData,[e.target.name]:e.target.value})
  }
  const onSubmit=async (e)=>{
    e.preventDefault()
    const newContact = {
      firstName:firstName,
    lastName:lastName,
    email:email,
    company:company,
    phone:phone, 
    }
  const config = {
    headers:{
      "Content-Type" : "application/json"
    },
  };
  try{
     const body = JSON.stringify(newContact)
     await axios.post('/contact',body,config)
     setFormData(
       {firstName:" ",
       lastName:" ",
       email:"",
       company:"",
       phone:"", 
       } 
     )
     window.location.reload()
  }catch(err){
    console.log("error",err.response.data)
  }
  } 
 const [contacts,setContacts] = useState([]) 



 const getAllContacts=  async () =>{
   const config = {
     headers:{
      "Content-Type":"application/json"
     }
   }
   try{
     const res = await axios.get('/contact',config)
      setContacts(res.data)
    }catch(err){
     console.log("error",err)
   }
 }
 useEffect(()=>{
   getAllContacts()
 },[])

 const [currentContact,setCurrentContact] = useState


  return (
        <>
          <form className='contact-form' onSubmit={(e)=>onSubmit(e)}>
          
          <input 
          type="text" 
          name="firstName" 
          value={firstName}
          placeholder="Enter your firstName"
          onChange = {(e) => onChange(e)}  
          
          ></input>
          <br/>
          <input 
          type="text" 
          name="lastName" 
          value={lastName}
          placeholder="Enter your lastName"
          onChange = {(e) => onChange(e)}  
          
          ></input>
          <br/>
          <input 
          type="text" 
          name="email" 
          value={email}
          placeholder="Enter your email"
          onChange = {(e) => onChange(e)}  
          required
          ></input>
          <br/>
          <input 
           type="text"
           name="company"
           value={company}
           placeholder="Enter your company name"
           onChange={(e)=>onChange(e)}
           required>
           </input>
           <br/>
           <input 
          type="tel" 
          name="phone" 
          value={phone}
          placeholder="Enter your phone no"
          onChange = {(e) => onChange(e)}  
          required
          ></input>
          <button type="submit">Add new Contact</button>
           

               </form>
               <div className='contacts-container'>
                   <ul className='contact-list'>
                      {contacts.length >0 ?contacts.map((contact)=>(
                        <li key={contact._id}>
                        <div className='left'>
                        <p>
                            {contact.firstName}
                            &nbsp;{contact.lastName}
                          </p>
                          <p>{contact.email}</p>
                          <p> Works at {contact.company}</p>
                          <p>{contact.phone}</p>
                        </div> 
                         </li>
                      )):<h1 style={{textAlign:"center",width:"90%",margin:"auto"}}>
                        No contacts found
                      </h1>}
                   </ul>
               </div>
        </>
    )
}
export default Contact
